import React from 'react';
import { useLocation } from 'react-router-dom';
import useFormWithValidation from '../../hooks/useFormWithValidation';
import './SearchForm.css';

export default function SearchForm(props) {
  const [name, setName] = React.useState('')
  const [checkbox, setCheckbox] = React.useState(false)

  function handleChangeName(e) {
    setName(e.target.value)
  }

  function handleChangeCheckbox(e) {
    const isShortFilms = e.target.checked
    setCheckbox(isShortFilms)
    props.handleSearch(name, isShortFilms)
  }

  function handleSubmit(e) {
    e.preventDefault()
    props.handleSearch(name, checkbox)
  }

  React.useEffect(() => {
    setName(props.defaultValue)
    setCheckbox(JSON.parse(localStorage.getItem('shortFilms')) || false)
  }, [])

  return(
    <section className='search-movies section-movies'>
      <div className='search-movies__container'>
        <form
          noValidate
          className='search-movies__form'
          onSubmit={handleSubmit}
        >
          <input
            className='search-movies__input'
            type='text'
            name='name'
            placeholder='Фильм'
            maxLength='60'
            onChange={handleChangeName}
            value={name || ''}
            autoComplete='off'
          />
          <button
            className='search-movies__button'
            type='submit'
            disabled={!isValid}
          >
            Найти
          </button>
        </form>
        <span className='search-movies__input-error'>{errors.name}</span>
        <div className='filter-ckeckbox'>
      <label className='filter-checkbox__label'>
        <input
          type='checkbox'
          className='filter-checkbox__input'
          name='filterCheckbox'
          checked={checked}
          onChange={(e) => onChange(e)}
        />
        <span className='filter-checkbox_visible'></span>
      </label>
      <p className='filter-checkbox__title'>Короткометражки</p>
    </div>
      </div>
    </section>
  )
}
